import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
const Signup = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Sending data:', formData); // Add this to confirm data is being sent  
    try{
    const res = await axios.post('http://localhost:5000/api/signup', formData);

    if (res.status === 201) {
      console.log('User signed up successfully:', res.data);
      navigate('/login');
    }
    }
    catch (error) {
      console.error('Error signing up user:', error);
    }
  };
  
  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      <nav className="w-full flex justify-between items-center px-8 py-4 bg-black">
        <h1 className="text-4xl font-extrabold text-white">
          <Link to="/">GHABATY</Link>
        </h1>
        <ul className="flex gap-8 text-lg text-white">
          {["Home", "Alerts", "Reports", "Contact", "About Us"].map((link) => (
            <li key={link} className="hover:text-red-400">
              <Link to={`/${link.toLowerCase().replace(/\s+/g, "-")}`}>
                {link}
              </Link>
            </li>
          ))}
        </ul>
      </nav>

      <section className="flex-grow flex items-center justify-center">
        <div className="bg-white p-8 rounded shadow-md w-full max-w-md">
          <h2 className="text-2xl font-bold mb-6 text-center">Sign Up</h2>
          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label className="block text-gray-700">Name</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded"
                required
              />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700">Email</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded"
                required
              />
            </div>
            <div className="mb-6">
              <label className="block text-gray-700">Password</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                className="w-full px-3 py-2 border rounded"
                required
              />
            </div>
            <button
              type="submit"
              className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600 transition duration-200"
            >
              Sign Up
            </button>
          </form>
          <p className="mt-4 text-center">
            Already have an account?{" "}
            <Link to="/login" className="text-blue-500 hover:underline">
              Log In
            </Link>
          </p>
        </div>
      </section>

      <footer className="bg-black text-gray-300 py-6">
        <div className="container mx-auto text-center">
          <p>&copy; 2024 Ghabaty. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default Signup;
